function getGitHubUser(id, callback) {
  setTimeout(() => {
    console.log("Reading user from database...");
    callback({id: id, githubUsername: 'faroooq'});
  }, 2000);
}

function getRepositories(user_name, callback) {
  setTimeout(() => {
    console.log("Reading repos from database...");
    callback({name: user_name, repositories: ['angular_poc', 'react_poc']});
  }, 2000);
}

function getCommits(repo_id, callback) {
  setTimeout(() => {
    console.log("Reading commits from database...");
    callback({id: repo_id, commits: ['initial commmit', 'dbchanges']});
  }, 2000);
}

getGitHubUser(1, (user) => {
    console.log("User: ", user);
    getRepositories(user.githubUsername, (repos) => {
      console.log("Repos: ", repos);
      getCommits(repos[0], (commits) => {
        console.log("Commits: ", commits);
        // Callback Hell ("-_-)
    })
  })
})

Output:
    Reading user from database...
    User: {id: 1, githubUsername: "faroooq"}
    Reading repos from database...
    Repos: {name: "faroooq", repositories: Array[2]}
    Reading commits from database...
    Commits: {id: "angular_poc", commits: Array[2]}

function getGitHubUser(id) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log("Suppose getting data from a database....");
      if(true) {
        resolve({ id: id, githubUsername: "faroooq" });
      } else {
        reject(new Error("No User Found"));
      }
    }, 2000);
  });
}

function getRepositories(username) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log(`Suppose getting Repositories data from db for ${username}...`);
      if(true) {
        resolve(["angular_poc", "react_poc", "java_poc"]);
      } else {
        reject(new Error("Error occured in getting repositories"));
      }
    }, 2000);
  });
}

function getCommits(repo) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log("Suppose getting commits for " + repo + "....");
      if(true) {
        resolve(["commits"]);
      } else {
        reject(new Error("Error occured in getting commits"));
      }
    }, 2000);
  });
}

// Replace Callback with Promises to avoid callback hell
getGitHubUser(1)
  .then((user) => getRepositories(user.githubUsername))
  .then((repos) => getCommits(repos[0]))
  .then((commits) => console.log("Commits: ", commits))
  .catch((err) => console.log("Error: ", err.message));

function getUser(id) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log('Suppose getting data from a database....');
      if (true) {
        resolve({ id: id, githubUsername: 'faroooq' });
      } else {
        reject(new Error('No User Found'));
      }
    }, 2000);
  });
}

function getRepositories(username) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log(
        `Suppose getting Repositories data from db for ${username}...`
      );
      if (true) {
        resolve(['angular_poc', 'react_poc', 'java_poc']);
      } else {
        reject(new Error('Error occured in getting repositories'));
      }
    }, 2000);
  });
}

function getCommits(repo) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log('Suppose getting commits for ' + repo + '....');
      if (true) {
        resolve(['commits']);
      } else {
        reject(new Error('Error occured in getting commits'));
      }
    }, 2000);
  });
}

// Async- await approach
async function getUserCommits() {
  try {
    const user = await getUser(1);
    const repos = await getRepositories(user.githubUsername);
    const commits = await getCommits(repos[0]);
    console.log(commits);
  } catch (err) {
    console.log('Error: ', err.message);
  }
}

getUserCommits();
