import { Component } from '@angular/core';

@Component({
  selector: 'app-item-list',
  templateUrl: './item-list.component.html',
  styleUrls: ['./item-list.component.css']
})
export class ItemListComponent {
  itemList: Array<{ name: string, price: string, hide: boolean }>;

  constructor() {
    this.itemList = [
      {
        name: "IPhone 12",
        price: "INR 56,000/-",
        hide: false
      },
      {
        name: "Samsung Galaxy S21 Ultra",
        price: "INR 88,000/-",
        hide: true
      },
      {
        name: "OnePlus 9 Pro",
        price: "INR 59,000/-",
        hide: false
      }
    ]
  }
  toggle(item: { hide: boolean; }) {
    item.hide = !item.hide;
  }
}
